INSERT INTO public.sections_services (page_slug, display_order, section_key, title, subtitle, bg_theme, specific_data, primary_cta_text, admin_instruction) VALUES
-- 1. Pre-Sales Transformation
('pre-sales', 1, 'hero', 'Build a High-Accuracy Go-To-Market Foundation', 'Align your brand promise, personas, messaging and expectation frameworks to create a Go-To-Market engine that consistently brings the right customers into your pipeline.', 'navy', '{"badge": "Pre-Sales Transformation"}', 'Book a GTM Strategy Review', 'Hero section for Pre-Sales page'),
('pre-sales', 2, 'problem_block', 'Why Pre-Sales Fails', 'Most Pre-Sales systems fail not because teams lack leads, but because they lack clarity and alignment.', 'light', '{"list": ["Clarity of promise (Brand Promise not grounded in reality)", "Clarity of persona (demographic personas instead of behavioral ones)", "Clarity of pitch (feature-first, expectation-last)", "Alignment across functions (Marketing → Sales → CS misaligned)", "Expectation Match (customer expectations ≠ organizational delivery)"]}', NULL, 'Problem statement block'),
('pre-sales', 3, 'approach_steps', 'Our Approach: Anchored in Frameworks', 'We use a systematic approach anchored 100% in your frameworks.', 'gray', '{"steps": [{"title": "CX Maturity Framework Diagnostic", "desc": "Baselines how well the organization articulates, communicates and delivers its Brand Promise across journeys."}, {"title": "Expectation Management Framework (EMM)", "desc": "Maps customer expectations against organizational delivery to identify high-impact GTM interventions."}, {"title": "Persona–Product–Pitch Playbook", "desc": "Defines who we sell to, what they expect, and how we pitch value using behavioral personas."}, {"title": "Peak Purpose + Leadership Alignment", "desc": "Vision → Mission → Values → Goals → OKRs workshop ensuring leaders share a north star."}, {"title": "Discovery & Proposal Storylines", "desc": "A repeatable GTM structure driven by expectation-based pitching, not features."}]}', NULL, 'Methodology/Approach steps'),
('pre-sales', 4, 'outcomes_grid', 'Measurable Impact', 'Outcomes driven by GTM transformation.', 'light', '{"stats": [{"label": "Marketing/BD Conversion", "value": "+15-25%"}, {"label": "Retention", "value": "+12-25%"}, {"label": "Funnel Predictability", "value": "+20-30%"}, {"label": "Decision Speed", "value": "+25%"}, {"label": "Handoff Friction", "value": "Lower"}]}', 'Explore All GTM Case Studies', 'Key impact statistics'),

-- 2. Sales Velocity Acceleration
('sales-velocity', 1, 'hero', 'Accelerate Sales Velocity With Value-Led Selling', 'Equip teams with the Value Realization narratives, expectation-aligned pitching, and sales governance required to shorten cycles and improve win rates.', 'navy', '{"badge": "Sales Velocity Acceleration"}', 'Book a Sales Velocity Audit', 'Hero section for Sales Velocity page'),
('sales-velocity', 2, 'problem_block', 'When Sales Velocity Suffers', 'Sales velocity suffers when discovery is shallow and expectations are misaligned.', 'light', '{"list": ["Discovery is shallow", "Expectations are not aligned early", "Every seller has their own pitch", "Value is not tied to customer KPIs", "Forecasting lacks consistency", "Sales → CS handoff is broken"]}', NULL, 'Problem statement block'),
('sales-velocity', 3, 'approach_steps', 'Our Approach: Value & Governance', 'Systematic intervention for speed, quality, and predictability.', 'gray', '{"steps": [{"title": "Persona–Product–Pitch (PPP) Playbook", "desc": "Moves sellers from \"feature demonstration\" to \"expectation alignment\"."}, {"title": "Value Realization Map (VRM™)", "desc": "Links capabilities to customer success metrics, business KPIs, and economics."}, {"title": "Expectation Management Framework", "desc": "Anticipates friction points + objections; improves proposal quality."}, {"title": "Sales Governance Rhythm", "desc": "Review templates, scorecards, forecast accuracy structures."}, {"title": "Sales → CS Alignment", "desc": "Integrated lifecycle flow using Customer Lifecycle Heatmap."}]}', NULL, 'Methodology/Approach steps'),
('sales-velocity', 4, 'outcomes_grid', 'Measurable Impact', 'Outcomes driven by sales transformation.', 'light', '{"stats": [{"label": "Sales Velocity", "value": "2x–5x"}, {"label": "Conversion Improvement", "value": "+15–30%"}, {"label": "Forecast Accuracy", "value": "+15–32%"}, {"label": "Close Rate", "value": "Higher"}]}', 'View Sales Impact Stories', 'Key impact statistics'),

