('customer-success', 1, 'hero', 'Transform Customer Success Into a Revenue Engine', 'Design segmentation, journeys, value realization, health scoring and advocacy motions that increase retention, expansion and Net Revenue Retention (NRR).', 'navy', '{"badge": "Customer Success Transformation"}', 'Take the CS Maturity Assessment', 'Hero section for CS page'),
('customer-success', 2, 'problem_block', 'The CS Struggle', 'Organizations struggle when CS is trapped in reactive modes.', 'light', '{"list": ["CS is trapped in ticket-solving mode", "No common lifecycle view exists", "Renewals are last-minute", "Value is not captured or communicated", "No advocacy or co-creation program exists", "Health score models are inaccurate"]}', NULL, 'Problem statement block'),
('customer-success', 3, 'approach_steps', 'Our Approach: The Post-Sales System', 'Operationalizing success for revenue and advocacy.', 'gray', '{"steps": [{"title": "Customer Success Maturity Model (31Q)", "desc": "Your canonical, 10-domain CS diagnostic."}, {"title": "Customer Lifecycle Heatmap", "desc": "Maps signals of adoption, usage, value, risk & expansion."}, {"title": "V.I.C.T.O.R.Y™ Framework", "desc": "Your proprietary 7-stage CS-to-Revenue framework."}, {"title": "Value Realization Map (VRM)", "desc": "Connects customer outcomes to your economic metrics."}, {"title": "9Ps Advocacy Framework", "desc": "Turns customers into co-creators, storytellers, advocates."}, {"title": "CX ROI Calculator", "desc": "Financial modeling of NPS → economics."}]}', NULL, 'Methodology/Approach steps'),
('customer-success', 4, 'outcomes_grid', 'Measurable Impact', 'Outcomes driven by CS transformation.', 'light', '{"stats": [{"label": "Retention", "value": "+12–25%"}, {"label": "NRR", "value": "+10–25%"}, {"label": "Churn Reduction", "value": "20–35%"}, {"label": "Expansion", "value": "+15–30%"}, {"label": "Advocacy", "value": "2–3x"}, {"label": "Platform Adoption", "value": "+23–28%"}]}', 'Explore Customer Success Case Studies', 'Key impact statistics'),

